import MUIDataTable from "mui-datatables";
import DateAndTimePickers from "./DatePicker";
import { makeStyles } from '@material-ui/core/styles';
import React from 'react';
import { createTheme, MuiThemeProvider } from '@material-ui/core/styles';



import {
    FormGroup,
    FormLabel,
    FormControl,
    ListItemText,
    TextField,
    Checkbox,
    FormControlLabel,
    Grid,
    Select,
    InputLabel,
    MenuItem
  } from '@material-ui/core';





  const getTheme = () => createTheme({
    overrides: {


        MuiTypography: {
            h6: {
                color: "#282828 !important",
                right: "30px",
                marginLeft: "-8px"

            },

            root: {
              margin:"0px"
            }
        },

        MuiPaper: {
          rounded: {
            borderRadius: "0px",
            width:"100%",

          }
        }

    }
});


  class Example extends React.Component {
    state = {
      TimestampFilterChecked: false
    };

    render() {
      const columns = [ "Defect Type", "ProductionLine",
                        {
                          name: 'Timestamp',
                          options: {
                          filter: true,
                          filterType: 'custom',
                          customFilterListOptions: {
                          render: v => {
                          if (v[0] && v[1] && this.state.TimestampFilterChecked) {
                          return [`Min Age: ${v[0]}`, `Max Age: ${v[1]}`];}
                          else if (v[0] && v[1] && !this.state.TimestampFilterChecked) {
                          return `Min Age: ${v[0]}, Max Age: ${v[1]}`;}
                          else if (v[0]) {
                          return `Min Age: ${v[0]}`;}
                          else if (v[1]) {
                          return `Max Age: ${v[1]}`;}
                          return [];},
                          update: (filterList, filterPos, index) => {
                          console.log('customFilterListOnDelete: ', filterList, filterPos, index);
                          if (filterPos === 0) {
                          filterList[index].splice(filterPos, 1, '');}  
                          else if (filterPos === 1) {
                          filterList[index].splice(filterPos, 1);} 
                          else if (filterPos === -1) {
                          filterList[index] = [];}
                          return filterList;},},
                          filterOptions: {
                          names: [],
                          logic(Timestamp, filters) {
                if (filters[0] && filters[1]) {
                  return Timestamp < filters[0] || Timestamp > filters[1];
                } else if (filters[0]) {
                  return Timestamp < filters[0];
                } else if (filters[1]) {
                  return Timestamp > filters[1];
                }
                return false;
              },
              display: (filterList, onChange, index, column) => (
                <div>
                  <FormLabel>Timestamp</FormLabel>
                  <FormGroup row>

                    <TextField
        id="datetime-local"
        label="Start Date"
        type="datetime-local"
        defaultValue="2017-05-24"
        value={filterList[index][0] || ''}
        onChange={event => {
            filterList[index][0] = event.target.value;
            onChange(filterList[index], index, column);
          }}
          InputLabelProps={{
            shrink: true,
          }}
          style={{ width: '45%' }}

      />
                    <TextField
                      id="datetime-local"
                      label="End Date"
                      type="datetime-local"
                      format="DD-MM-YYYY"
                      defaultValue="2017-05-24T10:30"
                      value={filterList[index][1] || ''}
                      onChange={event => {
                        filterList[index][1] = event.target.value;
                        onChange(filterList[index], index, column);
                      }}
                      style={{ width: '45%' }}
                      InputLabelProps={{
                        shrink: true,
                      }}
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                          checked={this.state.TimestampFilterChecked}
                          onChange={event => this.setState({ TimestampFilterChecked: event.target.checked })}
                        />
                      }
                      label='Separate Values'
                      style={{ marginLeft: '0px' }}
                    />
                  </FormGroup>

                </div>
              ),
            },
            print: false,
          },
        },

      ];
      var dt1 = new Date("2015-03-25T12:00:00Z").toISOString().split('.')[0];
      var dt2 = new Date("28 July 2021 15:05 UTC");

      dt2 = dt2.toISOString()
      const data = [
        [dt1],
        [dt2],
        [27],
        [22],
        [28, '$75,000'],
        [65, '$94,000'],
        [71, '$210,000'],
        [19, '$65,000'],
        [20, '$77,000'],
        [37, '$135,000'],
        [52, '$420,000'],
        [30, '$150,000'],
        [31, '$170,000'],
        [22, '$90,000'],
        [24, '$133,000'],
        [50, '$295,000'],
        [28, '$200,000'],
        [65, '$400,000'],
        [34, '$110,000'],
        [60, '$220,000'],
        [52, '$180,000'],
        [44, '$99,000'],
        [ 37, '$90,000'],
        [ 39, '$140,000'],
        [ 26, '$330,000'],
        [ 47, '$250,000'],
        [49, '$190,000'],
        [ 44, '$80,000'],
        [ 26, '$45,000'],
        [9, '$142,000'],
      ];

      const options = {
        filter: true,
        filterType: 'multiselect',
        responsive: 'standard',
        setFilterChipProps: (colIndex, colName, data) => {
          //console.log(colIndex, colName, data);
          return {
            color: 'primary',
            variant: 'outlined',
            className: 'testClass123',
          };
        }
      };

      return (
        <MuiThemeProvider theme={getTheme()}> 
        <MUIDataTable title={'Filter'} data={data} columns={columns} options={options} />
        </MuiThemeProvider>
      );
    }
  }

  export default Example;
