import React from 'react';

// nodejs library that concatenates classes
import classNames from "classnames";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";


import {
    Button,
    ButtonGroup,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    UncontrolledDropdown,
    Label,
    FormGroup,
    Input,
    Table,
    Row,
    Col,
    UncontrolledTooltip,
  } from "reactstrap";

  import {
    chartExample1,
    LineChart1,
    chartExample2,
    chartExample3,
    chartExample4,
    chart_MultiAxisLine_1_Hourly
  } from "variables/charts.js";


  import { useHistory } from "react-router-dom";


const DefectRate = ({title}) => {
    const [bigChartData, setbigChartData] = React.useState("HourlyData");
    const [bigChartData1, setbigChartData1] = React.useState("HourlyData");
    const [bigChartData11, setbigChartData11] = React.useState("HourlyData");

    const setBgChartData = (name) => {
      setbigChartData(name);
    };
    const setBgChartData1 = (name) => {
      setbigChartData1(name);
    };
    const setBgChartData11 = (name) => {
        setbigChartData11(name);
      };


      if (title == 4){
        return (
            <>



            <Col xs="12">
              <Card className="card-chart">
                <CardHeader>
                  <Row>
                    <Col className="text-left" sm="6">
                      <CardTitle tag="h3">Defect Rate </CardTitle>
                    </Col>
                    <Col sm="6">

                    </Col>
                  </Row>
                </CardHeader>
                <CardBody>
                  <div className="chart-area">
                    <Line
                      data={chartExample1[bigChartData]}
                      options={chartExample1.options}
                    />
                  </div>
                </CardBody>

              </Card>
            </Col>
            <Col xs="12">
              <Card className="card-chart">
                <CardHeader>
                  <Row>
                    <Col className="text-left" sm="6">
                      <CardTitle tag="h3">Cumulative Yield vs Target Yield</CardTitle>
                    </Col>
                    <Col sm="6">

                    </Col>
                  </Row>
                </CardHeader>
                <CardBody>
                  <div className="chart-area">
                    <Line
                      data={chart_MultiAxisLine_1_Hourly[bigChartData11]}
                      options={chart_MultiAxisLine_1_Hourly.options}
                    />
                  </div>
                </CardBody>

              </Card>
            </Col>
     </>
        )
    }


    else{
    return (
        null


    );}
};

export default DefectRate;
