import React, { useState } from "react";

import classNames from "classnames";
import ProductionOutput from "./sub-views/ProductionOutput";
import ProductionTime from "./sub-views/ProductionTime";
import Defects from "./sub-views/Defects";
import DefectRate from "./sub-views/DefectRate";




// reactstrap components
import {
  Button,
  ButtonGroup,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  Label,
  FormGroup,
  Input,
  Table,
  Row,
  Col,
  UncontrolledTooltip,
} from "reactstrap"; 



function Dashboard(props) {
  const [active, setActive] = useState("ProductionOutputButton")

  return (
    <>

      <div className="content">
      <Row>


          <Col lg="auto">

          <Button
                        tag="label"
                        className={classNames("btn-simple", {
                          active: active === "ProductionOutputButton" && <Card title="1" />

                        })}
                        color="info"
                        id="0"
                        size="lg"


                        onClick={() => setActive("ProductionOutputButton")}
                      >



                        <span className="d-block d-sm-none">
                          <i className="tim-icons icon-app" />
                        </span>
                        <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">

                        <span>Production Output</span> 


                        </span>
            </Button>
          </Col>
          <Col lg="auto">

            <Button
                          tag="label"
                          className={classNames("btn-simple", {
                            active: active === "ProductionTimeButton" && <Card title="2" />

                          })}
                          color="info"
                          id="0"
                          size="lg"


                          onClick={() => setActive("ProductionTimeButton")}
                        >



                          <span className="d-block d-sm-none">
                            <i className="tim-icons icon-app" />
                          </span>
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">

                          <span>Production Time</span> 


                          </span>
              </Button>
            </Col>


            <Col lg="auto">

            <Button
                          tag="label"
                          className={classNames("btn-simple", {
                            active: active === "DefectsButton" && <Card title="3" />

                          })}
                          color="info"
                          id="0"
                          size="lg"


                          onClick={() => setActive("DefectsButton")}
                        >



                          <span className="d-block d-sm-none">
                            <i className="tim-icons icon-app" />
                          </span>
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">

                            Defects  

                          </span>
              </Button>
            </Col>
            <Col>

            <Button
                          tag="label"
                          className={classNames("btn-simple", {
                            active: active === "DefectRate" && <Card title="4" />

                          })}
                          color="info"
                          id="0"
                          size="lg"


                          onClick={() => setActive("DefectRate")}
                        >



                          <span className="d-block d-sm-none">
                            <i className="tim-icons icon-app" />
                          </span>
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">

                            Defect Rate     

                          </span>
              </Button>
            </Col>


          {active === "ProductionOutputButton" && <ProductionOutput title="1" />}
          {active === "ProductionTimeButton" && <ProductionTime title="2" />}
          {active === "DefectsButton" && <Defects title="3" />}
          {active === "DefectRate" && <DefectRate title="4" />}



          </Row>

      </div>
    </>
  );
}

export default Dashboard;
