import React, { Component } from "react";
import Chart from "react-apexcharts";
import "./HorizontalBarChart.css";
import * as FaIcons from 'react-icons/fa';



class HorizontalBarChart extends Component {
    constructor(props) {
      super(props);

      this.state = {

        series: [{
          name: 'POCK',
          data: [44]
        }, {
          name: 'SSCT',
          data: [53]
        }, {
          name: 'POGR',
          data: [12]
        }, {
          name: 'STAN',
          data: [9]
        }, {
          name: 'OTHERS',
          data: [27]
        }],
        options: {
          chart: {
            type: 'bar',
            height: 350,
            stacked: true,
            stackType: '100%'
          },
          plotOptions: {
            bar: {
              horizontal: true,
            },
          },
          stroke: {
            width: 1,
            colors: ['#fff']
          },
          title: {
            text: '100% Stacked Bar'
          },
          xaxis: {
            categories: [""],
            show: false,

            labels: {
                show: false,
            }
          },
          yaxis: {
            categories: ["1"],
            show: false,

            labels: {
                show: false,
            }
          },

          grid:{
            show:false,
            borderColor: "#000000",
            strokeDashArray: 0,
            xaxis: {
                lines: {
                    show: false,
                },
            },
            yaxis: {
                lines: {
                    show: true,
                },
            },
            row: {
                colors: "#000000",
                opacity: 0.0
            },  
          },


          tooltip: {
            y: {

            }
          },
          fill: {
            opacity: 1

          },
          legend: {
            position: 'top',
            horizontalAlign: 'center',
            offsetX: 40
          },
          colors: [
            "#111111", "#333333", "#555555", "#777777", "#999999",
          ],
        },


      };
    }

    render() {
      return (
        <div className="app">
          <div className="row">
            <div className="mixed-chart">
              <Chart
                options={this.state.options}
                series={this.state.series}
                type="bar"
                height="250"
                width= "1129"
              />
            </div>
          </div>
        </div>
      );
    }
  }

  export default HorizontalBarChart;
