import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
import "assets/css/myStyle.css"


// reactstrap components
import {
  Button,
  ButtonGroup,
  Collapse,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  Input,
  InputGroup,
  NavbarBrand,
  Navbar,
  NavLink,
  Nav,
  Container,
  Modal,
  NavbarToggler,
  ModalHeader,

} from "reactstrap";

import {
  LineChart2,
  LineChart1,
  LineChart3,
  LineChart4,
  BarChart1,
  MultiAxisLineChart
} from "variables/charts.js";

function AdminNavbar(props) {

  const [MultiAxisLineChart, setMultiAxisLineChart] = React.useState("HourlyData");
  const set_Main_MultiAxisLineChart = (name) => {
    setMultiAxisLineChart(name);
  };

  const [LineChart1, setLineChart1] = React.useState("HourlyData");
  const set_Main_LineChart1 = (name) => {
    setLineChart1(name);
  };

  const [LineChart2, setLineChart2] = React.useState("HourlyData");
  const set_Main_LineChart2 = (name) => {
    setLineChart2(name);
  };

  const [LineChart3, setLineChart3] = React.useState("HourlyData");
  const set_Main_LineChart3 = (name) => {
    setMultiAxisLineChart(name);
  };



  const [collapseOpen, setcollapseOpen] = React.useState(false);
  const [modalSearch, setmodalSearch] = React.useState(false);
  const [color, setcolor] = React.useState("navbar-transparent");
  React.useEffect(() => {
    window.addEventListener("resize", updateColor);
    // Specify how to clean up after this effect:
    return function cleanup() {
      window.removeEventListener("resize", updateColor);
    };
  });

  // function that adds color white/transparent to the navbar on resize (this is for the collapse)
  const updateColor = () => {
    if (window.innerWidth < 993 && collapseOpen) {
      setcolor("bg-white");
    } else {
      setcolor("navbar-transparent");
    }
  };

  // this function opens and closes the collapse on small devices
  const toggleCollapse = () => {
    if (collapseOpen) {
      setcolor("navbar-transparent");
    } else {
      setcolor("bg-white");
    }
    setcollapseOpen(!collapseOpen);
  };


  return (
    <>
      <Navbar className={classNames("navbar-absolute", color)} expand="lg">
        <Container fluid>
          <div className="navbar-wrapper">
            <div
              className={classNames("navbar-toggle d-inline", {
                toggled: props.sidebarOpened,
              })}
            >
              <NavbarToggler onClick={props.toggleSidebar}>
                <span className="navbar-toggler-bar bar1" />
                <span className="navbar-toggler-bar bar2" />
                <span className="navbar-toggler-bar bar3" />
              </NavbarToggler>

            </div>
            <NavbarBrand href="#pablo" onClick={(e) => e.preventDefault()}>
              {props.brandText}
            </NavbarBrand>
          </div>

          <Collapse navbar isOpen={collapseOpen}>

            <Nav className="ml-auto" navbar>

              <UncontrolledDropdown nav>

              <div>
                  <dropdown>
                    <select>
                      <option>Production Line: All</option>
                      <option>Production Line: 1</option>
                    </select>
                  </dropdown>
                </div>

              <ButtonGroup
                        className="btn-group-toggle float-right"
                        data-toggle="buttons"
                      >
                        <Button
                          tag="label"
                          className={classNames("btn-simple", {
                            active: MultiAxisLineChart === "HourlyData",
                          })}
                          color="info"
                          id="0"
                          size="sm"
                          onClick={() => {set_Main_MultiAxisLineChart("HourlyData");}}
                        >
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Hourly
                          </span>

                        </Button>
                        <Button
                          color="info"
                          id="1"
                          size="sm"
                          tag="label"
                          className={classNames("btn-simple", {
                            active: MultiAxisLineChart === "DailyData",
                          })}
                          onClick={() => {set_Main_MultiAxisLineChart("DailyData");}}
                        >
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Daily
                          </span>

                        </Button>
                        <Button
                          color="info"
                          id="2"
                          size="sm"
                          tag="label"
                          className={classNames("btn-simple", {
                            active: MultiAxisLineChart === "MonthlyData",
                          })}
                          onClick={() => {set_Main_MultiAxisLineChart("MonthlyData");}}
                        >
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Monthly
                          </span>
                        </Button>
                      </ButtonGroup>
              </UncontrolledDropdown>
              <li className="separator d-lg-none" />
            </Nav>
          </Collapse>
        </Container>
      </Navbar>
    </>
  );
}

export default AdminNavbar;
