import React from 'react';
import ReactDOM from 'react-dom';
import {colors} from 'react-select';
import { VictoryPie, VictoryLabel } from 'victory';


const data = [
    {x: 1, y: 1},
    {x: 2, y: 2},
    {x: 3, y: 3},
    {x: 4, y: 4},
    {x: 5, y: 5}
  ];
  let colorScales = data.map((item) => item.color);
  function pieLabelRadius(fraction) {
    if (fraction === 1) {
        return 0;
    } else if (fraction >= .5) {
        return 0.5;
    } else {
        const t = Math.tan(fraction * Math.PI / 2);
        return (2*t) / ((1+t)*(1+t));
    }
}



class PieChart extends React.Component {


    render() {
      return (

        <>

        <VictoryPie 

          width={250} height={200}
          data={data}

          labels={({ datum }) => `Defect: ${datum.y}`}

          innerRadius={0} labelRadius={30}
          style={{ labels: { fontSize: 10, fill: "white" } }}

        />

        <VictoryLabel
          textAnchor="middle"
          style={{ fontSize: 100 }}
          x={200} y={200}
          text="Pie!"

        />
        </>
      )
    }
}

export default PieChart;
