import React, { useState } from "react";

import classNames from "classnames";
import ProductionOutput from "./ProductionOutput";






// reactstrap components
import {
  Button,
  ButtonGroup,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  Label,
  FormGroup,
  Input,
  Table,
  Row,
  Col,
  UncontrolledTooltip,
} from "reactstrap"; 


// core components
import {
  chartExample1,
  chartExample10,
  chartExample2,
  chartExample3,
  chartExample4,
} from "variables/charts.js";


import { useHistory } from "react-router-dom";




function Dashboard(props) {
  const [active, setActive] = useState("FirstCard")
  // const [bigChartData, setbigChartData] = React.useState("");
  // const [bigChartData1, setbigChartData1] = React.useState("data11");

  // const setBgChartData = (name) => {
  //   setbigChartData(name);
  // };
  // const setBgChartData1 = (name) => {
  //   setbigChartData1(name);
  // };
  return (
    <>
      <div className="content">
      <Row>


          <Col lg="auto">

          <Button
                        tag="label"
                        className={classNames("btn-simple", {
                          active: active === "FirstCard" && <Card title="1" />

                        })}
                        color="info"
                        id="0"
                        size="lg"


                        onClick={() => setActive("FirstCard")}
                      >



                        <span className="d-block d-sm-none">
                          <i className="tim-icons icon-app" />
                        </span>
                        <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">

                        <span>Production Output</span> 


                        </span>
            </Button>
          </Col>
          <Col lg="auto">

            <Button
                          tag="label"
                          className={classNames("btn-simple", {
                            active: active === "SecondCard" && <Card title="2" />

                          })}
                          color="info"
                          id="0"
                          size="lg"


                          onClick={() => setActive("SecondCard")}
                        >



                          <span className="d-block d-sm-none">
                            <i className="tim-icons icon-app" />
                          </span>
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">

                          <span>Production Time</span> 


                          </span>
              </Button>
            </Col>


            <Col lg="auto">

            <Button
                          tag="label"
                          className={classNames("btn-simple", {
                            active: active === "ThirdCard" && <Card title="3" />

                          })}
                          color="info"
                          id="0"
                          size="lg"


                          onClick={() => setActive("ThirdCard")}
                        >



                          <span className="d-block d-sm-none">
                            <i className="tim-icons icon-app" />
                          </span>
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">

                            Defects  

                          </span>
              </Button>
            </Col>
            <Col>

            <Button
                          tag="label"
                          className={classNames("btn-simple", {
                            active: active === "4" 

                          })}
                          color="info"
                          id="0"
                          size="lg"


                          onClick={() => setActive("4")}
                        >



                          <span className="d-block d-sm-none">
                            <i className="tim-icons icon-app" />
                          </span>
                          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">

                            Defect Rate     

                          </span>
              </Button>
            </Col>


          {active === "FirstCard" && <ProductionOutput title="1" />}
          {active === "SecondCard" && <ProductionTime title="2" />}
          {/* {active === "SecondCard" && <CardA title="3" />} */}

          </Row>

      </div>
    </>
  );
}

export default Dashboard;
