import React from 'react'
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import './InfoCard.scss'

function DetermineColor({icon, title, progress, text}){
    constprogressvalue = {progress}
    if(progressvalue < 30){
        buildStyles({

            pathColor: `rgba(1, 152, 117, 1)`,
            textColor: 'green'
        })
    }

    if(progressvalue > 31 && progressvalue < 70){

        buildStyles({

            pathColor: `rgba(211, 84, 0, 1)`,
            textColor: 'orange'
        })
    }

    if(progressvalue > 71 && progressvalue < 100){

        buildStyles({

            pathColor: `rgb(139, 0, 0)`,
            textColor: 'red'
        })
    }


}

const InfoCard = ({icon, title, progress, text}) => {

    const Icon = icon

    return (
        <div className='infocard'>

            <Icon className='card-icon' size={30}/>

            <CircularProgressbar 
            className='progressbar1' 
            value={progress} 
            text={text}
            strokeWidth={12}
            styles={DetermineColor} />

            <h5 className='card-title'>{title}</h5>


        </div>
    )
}


const progressBarStyles = buildStyles({


    pathColor: `rgba(0, 0, 0, 1)`,
    textColor: 'black'
})






export default InfoCard
