import React from 'react';
import { Line } from "react-chartjs-2";
import "./ProductionOutput.css"
import * as FaIcons from 'react-icons/fa'



import {
  LineChart_ProductionOutput, 
} from "variables/charts";


const ProductionOutput = ({LineChart_ProductionOutput_Example1, ProductionLineState}) => {
  var LineChart_ProductionOutput_Example1 = LineChart_ProductionOutput_Example1;
if(LineChart_ProductionOutput_Example1=="HourlyData" && ProductionLineState == "All"){
    var title = "Hourly";
    var SelectedProductionLine = LineChart_ProductionOutput;

}
if(LineChart_ProductionOutput_Example1=="DailyData"){
    var title = "Daily";
}
if(LineChart_ProductionOutput_Example1=="MonthlyData"){
    var title = "Monthly";
}
        return (
    <>
            {ProductionLineState}
            <div className='ProductionOutput'>

                <div className='ProductionOutput-header'>
                  <FaIcons.FaChartArea className='ProductionOutput-icon' size={20} color={'white'}/>
                  <h3 className='ProductionOutput-title'>{title} Production Output Report</h3>
                </div>

              <div className="ProductionOutput-body">
                  <div className="ProductionOutput-chart">
                    <Line
                      data={SelectedProductionLine[LineChart_ProductionOutput_Example1]}
                      options={SelectedProductionLine.options}
                    />
                  </div>

              </div>
            </div>

     </>
        )

};

export default ProductionOutput;
