import classNames from "classnames";
import {
    Button,
    ButtonGroup,
    Row,
    UncontrolledDropdown,
    Col,
  } from "reactstrap";



import PieChart1 from './subviews/PieChart/PieChart';
import ProgressBar from './subviews/ProgressBar/ProgressBar';
import Chart1 from './subviews/Chart1/Chart1';


function DefectAnalytics() {

    return(
        <>        <div style={{position:"relative",left:"0px", top:"-15px", height:"0"}}>
                  <div style={{position:"relative",left:"910px", top:"54px", height:"31.5px"}}>
