                        <option>Production Line: 1</option>

                      </select>
                    </dropdown>
                  </div>
                  <div style={{position:"absolute",left:"660px", top:"54px", height:"31.5px"}}>
                    <dropdown>
                      <select onChange={(e) => {
                        const SelectedProductionLine = e.target.value;
                        SetProductionLineState(SelectedProductionLine);

                      }}>
                        <option value="All">Defect: All</option>
                        <option value="1">Defect: 1</option>
                        <option value="2">Defect: 2</option>
                      </select>
                    </dropdown>
                  </div>
                  </div>
            <div class="content">


                <ButtonGroup style={{top:"-70.5px", left:"28px", position:"relative", width:"228px"}}
                className="btn-group-toggle float-right"
                data-toggle="buttons"
                >

                    <Button
                     tag="label"
                     className={classNames("btn-simple", {
                     active: "Daily"
                     })}
                     color="info"
                     id="0"
                     size="sm"
                     onClick={() => {"" 
