import React, { useState } from "react";

import classNames from "classnames";
import ProductionOutput from "./sub-views/ProductionOutput";
import ProductionTime from "./sub-views/ProductionTime";
import Defects from "./sub-views/Defects";
import DefectRate from "./sub-views/DefectRate";

// reactstrap components
import {
  Button,
  ButtonGroup,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  Label,
  FormGroup,
  Input,
  Table,
  Row,
  Col,
  UncontrolledTooltip,
} from "reactstrap";

function Dashboard() {
  const [active, setActive] = useState("ProductionOutputButton");
  const [MultiAxisLineChart_Example1, setMultiAxisLineChart] =
    React.useState("HourlyData");
  const set_Main_MultiAxisLineChart = (name) => {
    setMultiAxisLineChart(name);
  };

  const [LineChart_ProductionOutput_Example1, LineChart_ProductionOutput] =
    React.useState("HourlyData");
  const set_Main_LineChart_ProductionOutput = (name) => {
    LineChart_ProductionOutput(name);
  };

  const [LineChart_ProductionTime_Example1, LineChart_ProductionTime] =
    React.useState("HourlyData");
  const set_Main_LineChart_ProductionTime = (name) => {
    LineChart_ProductionTime(name);
  };

  const [LineChart_Defects_Example1, LineChart_Defects] =
    React.useState("HourlyData");
  const set_Main_LineChart_Defects = (name) => {
    LineChart_Defects(name);
  };

  const [LineChart_DefectRate_Example1, LineChart_DefectRate] =
    React.useState("HourlyData");
  const set_Main_LineChart_DefectRate = (name) => {
    LineChart_DefectRate(name);
  };



  const ProductionOutputData = Math.floor(Math.random() * 1000 + 1);
  const ProductionTimeData = Math.floor(Math.random() * 20 + 1);
  const DefectsData = Math.floor(Math.random() * 1000 + 1);
  const DefectRateData = Math.floor(Math.random() * 99 + 1);

  return (
    <>

      <div className="content">
        <Row>
          <Col lg="auto">
            <Button
              tag="label"
              className={classNames("btn", {
                active: active === "ProductionOutputButton" && (
                  <Card title="1" />
                ),
              })}
              color="info"
              id="0"
              size="lg"
              onClick={() => setActive("ProductionOutputButton")}
            >
              <span className="d-block d-sm-none">
                <i className="tim-icons icon-app" />
              </span>
              <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                <spanlarge>Production Output</spanlarge>
                <spandata>{ProductionOutputData}</spandata>
              </span>
            </Button>
          </Col>
          <Col lg="auto">
            <Button
              tag="label"
              className={classNames("btn", {
                active: active === "ProductionTimeButton" && <Card title="2" />,
              })}
              color="info"
              id="0"
              size="lg"
              onClick={() => setActive("ProductionTimeButton")}
            >
              <span className="d-block d-sm-none">
                <i className="tim-icons icon-app" />
              </span>
              <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                <spanlarge>Production Time</spanlarge>
                <spandata>{ProductionTimeData} Hours</spandata>
              </span>
            </Button>
          </Col>

          <Col lg="auto">
            <Button
              tag="label"
              className={classNames("btn", {
                active: active === "DefectsButton" && <Card title="3" />,
              })}
              color="info"
              id="0"
              size="lg"
              onClick={() => setActive("DefectsButton")}
            >
              <span className="d-block d-sm-none">
                <i className="tim-icons icon-app" />
              </span>
              <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                <spanlarge>Defects</spanlarge>
                <spandata>{DefectsData}</spandata>
              </span>
            </Button>
          </Col>
          <Col>
            <Button
              tag="label"
              className={classNames("btn", {
                active: active === "DefectRate" && <Card title="4" />,
              })}
              color="info"
              id="0"
              size="lg"
              onClick={() => setActive("DefectRate")}
            >
              <span className="d-block d-sm-none">
                <i className="tim-icons icon-app" />
              </span>
              <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                <spanlarge>Defect Rate</spanlarge>
                <spandata>{DefectRateData}%</spandata>
              </span>
            </Button>
          </Col>

          {active === "ProductionOutputButton" && (
            <ProductionOutput
              title="1"
              LineChart_ProductionOutput_Example1={
                LineChart_ProductionOutput_Example1
              }
              LineChart_ProductionTime_Example1={
                LineChart_ProductionTime_Example1
              }
              LineChart_Defects_Example1={LineChart_Defects_Example1}
              LineChart_DefectRate_Example1={LineChart_DefectRate_Example1}
            />
          )}
          {active === "ProductionTimeButton" && (
            <ProductionTime
              title="2"
              LineChart_ProductionOutput_Example1={
                LineChart_ProductionOutput_Example1
              }
              LineChart_ProductionTime_Example1={
                LineChart_ProductionTime_Example1
              }
              LineChart_Defects_Example1={LineChart_Defects_Example1}
              LineChart_DefectRate_Example1={LineChart_DefectRate_Example1}
            />
          )}
          {active === "DefectsButton" && (
            <Defects
              title="3"
              LineChart_ProductionOutput_Example1={
                LineChart_ProductionOutput_Example1
              }
              LineChart_ProductionTime_Example1={
                LineChart_ProductionTime_Example1
              }
              LineChart_Defects_Example1={LineChart_Defects_Example1}
              LineChart_DefectRate_Example1={LineChart_DefectRate_Example1}
            />
          )}
          {active === "DefectRate" && (
            <DefectRate
              title="4"
              LineChart_ProductionOutput_Example1={
                LineChart_ProductionOutput_Example1
              }
              LineChart_ProductionTime_Example1={
                LineChart_ProductionTime_Example1
              }
              LineChart_Defects_Example1={LineChart_Defects_Example1}
              LineChart_DefectRate_Example1={LineChart_DefectRate_Example1}
            />
          )}
        </Row>
      </div>
    </>
  );
}

export default Dashboard;
