import './DefectAnalytics.css';
import React, { useState } from "react";
import classNames from "classnames";
import {
    Button,
    ButtonGroup,
    Row,
    UncontrolledDropdown,
    Col,
  } from "reactstrap";



import PieChart1 from './subviews/PieChart/PieChart';
import ProgressBar from './subviews/ProgressBar/ProgressBar';
import ProgressBar2 from './subviews/ProgressBar2/ProgressBar2';
import Chart1 from './subviews/Chart1/Chart1';


function DefectAnalytics() {
  const [ProductionLineState, SetProductionLineState] = React.useState("All");
  const [DefectState, SetDefectState] = React.useState("All");
    return(
        <>        <div style={{position:"relative",left:"0px", top:"-15px", height:"0"}}>
                  <div style={{position:"relative",left:"910px", top:"54px", height:"31.5px"}}>
                    <dropdown>
                      <select onChange={(e) => {
                        const SelectedProductionLine = e.target.value;
                        SetProductionLineState(SelectedProductionLine);

                      }}>
                        <option value="All">Production Line: All</option>
                        <option value="1">Production Line: 1</option>
                        <option value="2">Production Line: 2</option>

                      </select>
                    </dropdown>
                  </div>
                  <div style={{position:"absolute",left:"660px", top:"54px", height:"31.5px"}}>
                    <dropdown>
                      <select onChange={(e) => {
                        const SelectedDefectState = e.target.value;
                        SetDefectState(SelectedDefectState);

                      }}>
                        <option value="All">Defect: All</option>
                        <option value="1">Defect: 1</option>
                        <option value="2">Defect: 2</option>
                      </select>
                    </dropdown>
                  </div>
                  </div>
            <div class="content">


                <ButtonGroup style={{top:"-70.5px", left:"28px", position:"relative", width:"228px"}}
                className="btn-group-toggle float-right"
                data-toggle="buttons"
                >

                    <Button
                     tag="label"
                     className={classNames("btn-simple", {
                     active: "Daily"
                     })}
                     color="info"
                     id="0"
                     size="sm"
                     onClick={() => {"" 
                     }}
                     >
                        <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                        Hourly
                        </span>

                    </Button>       

                    <Button
                     tag="label"
                     className={classNames("btn-simple", {
                     active: ""
                     })}
                     color="info"
                     id="0"
                     size="sm"
                     onClick={() => {"" 
                     }}
                     >
                        <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                        Daily
                        </span>

                    </Button>     


                    <Button
                     tag="label"
                     className={classNames("btn-simple", {
                     active: ""
                     })}
                     color="info"
                     id="0"
                     size="sm"
                     onClick={() => {"" 
                     }}
                     >
                        <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                        Monthly
                        </span>

                    </Button>     



                </ButtonGroup>
                <Col style={{display:"inline-block"}}>
                <Row >
                <PieChart1 DefectState={DefectState} ProductionLineState={ProductionLineState}/>
                <ProgressBar2 DefectState={DefectState} ProductionLineState={ProductionLineState}/>
                <ProgressBar DefectState={DefectState} ProductionLineState={ProductionLineState}/>
                </Row>
                </Col>

                <Row className="card-view">

                <Chart1 />


                </Row>
            </div>
        </>
    )

};

export default DefectAnalytics;
