import React from 'react'
import * as FaIcons from 'react-icons/fa'
import InfoCard from './InfoCard'
import './InfoCardRow.scss'

const infoCardsData_ProductionLineAll = [

    {icon: FaIcons.FaDropbox, title: 'Production Output', text: '25.3k', progress: 25, color:`rgba(120,0,0)`, textcolor:'rgba(0,0,0)'},
    {icon: FaIcons.FaClock, title: 'Production Time', text: '12', progress: 50, color:`rgba(247, 202, 24, 1)`, textcolor:'rgba(247, 202, 24, 1)'},
    {icon: FaIcons.FaInbox, title: 'Defects', text: '400', progress: 10, color:`rgba(30, 130, 76, 1)`, textcolor:'rgba(30, 130, 76, 1)'},
    {icon: FaIcons.FaChartLine, title: 'Defect Rate', text: '19.9%', progress: 19.9, color:`rgba(30, 130, 76, 1)`, textcolor:'rgba(30, 130, 76, 1)'},
    {icon: FaIcons.FaRedoAlt, title: 'Cycle Time', text: '5.5s', progress: 20, color:`rgba(30, 130, 76, 1)`, textcolor:'rgba(30, 130, 76, 1)'},
    ]

const infoCardsData_ProductionLine1 = [
    {icon: FaIcons.FaDropbox, title: 'Production Output', text: '13.3k', progress: 13, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    {icon: FaIcons.FaClock, title: 'Production Time', text: '12', progress: 50, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    {icon: FaIcons.FaInbox, title: 'Defects', text: '330', progress: 12, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    {icon: FaIcons.FaChartLine, title: 'Defect Rate', text: '89.9%', progress: 13, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    {icon: FaIcons.FaRedoAlt, title: 'Cycle Time', text: '9.5s', progress: 67, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    ]

const infoCardsData_ProductionLine2 = [
    {icon: FaIcons.FaDropbox, title: 'Production Output', text: '27.3k', progress: 27, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    {icon: FaIcons.FaClock, title: 'Production Time', text: '13', progress: 60, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    {icon: FaIcons.FaInbox, title: 'Defects', text: '150', progress: 22, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    {icon: FaIcons.FaChartLine, title: 'Defect Rate', text: '89.9%', progress: 63, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    {icon: FaIcons.FaRedoAlt, title: 'Cycle Time', text: '5.5s', progress: 7, color:`rgba(0, 0, 0, 1)`, textcolor:'black'},
    ]



const InfoCardRow = ({ ProductionLineState }) => {
if(ProductionLineState=="All"){
    var infoCardsData = infoCardsData_ProductionLineAll;
}

if(ProductionLineState=="1"){
    var infoCardsData = infoCardsData_ProductionLine1;
}

if(ProductionLineState=="2"){
    var infoCardsData = infoCardsData_ProductionLine2;
}

    return (
        <div className='infocardrow'>

            <div className='infocardrow-header'>
                <FaIcons.FaInfoCircle className='infocardrow-icon' size={20} color={'white'}/>
                <h3 className='infocardrow-title'>Daily Report</h3>
            </div>

            <div className='infocardrow-body'>
                {
                    infoCardsData.map((infoCardData) => (
                        <InfoCard
                            icon={infoCardData.icon}
                            title={infoCardData.title}
                            text={infoCardData.text}
                            progress={infoCardData.progress}
                            color={infoCardData.color}
                            textcolor={infoCardData.textcolor}

                        />
                    ))
                }
            </div>

        </div>
    )
}


export default InfoCardRow
