import React, { Component } from "react";
import Chart from "react-apexcharts";
import "./HorizontalBarChart.css";
import * as FaIcons from 'react-icons/fa';



class HorizontalBarChart extends Component {
    constructor(props) {
      super(props);

      this.state = {

        series: [{
          name: 'POCK',
          data: [44]
        }, {
          name: 'SSCT',
          data: [53]
        }, {
          name: 'POGR',
          data: [12]
        }, {
          name: 'STAN',
          data: [9]
        }, {
          name: 'OTHERS',
          data: [27]
        }],
        options: {
          chart: {
            type: 'bar',
            height: 350,
            stacked: true,
            stackType: '100%',
            toolbar: {show: false},
          },
          plotOptions: {
            bar: {
              horizontal: true,
              borderRadius: 'rounded'
            },
          },
          stroke: {
            width: 1,
            colors: ['#fff']
          },
          title: {
            text: ''
          },
          xaxis: {
            categories: [""],
            show: false,

            labels: {
                show: false,
            },
            axisBorder: {
                show: false
            },
          },


          grid:{
            show:false,
            borderColor: "#000000",
            strokeDashArray: 0,
            xaxis: {
                lines: {
                    show: false,
                },
            },
            yaxis: {
                lines: {
                    show: true,
                },
            },
            row: {
                colors: "#000000",
                opacity: 0.0
            },  
          },


          tooltip: {
            y: {

            }
          },
          fill: {
            opacity: 1

          },
          legend: {
            position: 'top',
            horizontalAlign: 'center',
            offsetX: 40,
            offsetY: 20,
            fontSize: '15px',
            fontFamily: "Roboto",
            itemMargin: {horizontal: 10}

          },
          colors: [
            "#111111", "#333333", "#555555", "#777777", "#999999",
          ],
        },


      };
    }

    render() {
      return (

            <div className="HorizontalBarChart" >
                <div className='HorizontalBarChart-header'>
                    <FaIcons.FaPercentage className='HorizontalBarChart-icon' size={20} color={'white'}/>
                    <h3 className='HorizontalBarChart-title'>Defect Rate</h3>
                </div>

                <div className='HorizontalBarChart-body'>

                        <Chart
                        options={this.state.options}
                        series={this.state.series}
                        type="bar"
                        height="240"
                        width= "1129"

                    />

              </div>
              </div>

      );
    }
  }

  export default HorizontalBarChart;
