import React, { useState } from "react";

import classNames from "classnames";
import ProductionOutput from "./sub-views/ProductionOutput";
import ProductionTime from "./sub-views/ProductionTime";
import Defects from "./sub-views/Defects";
import DefectRate from "./sub-views/DefectRate";

// reactstrap components
import {
  Button,
  ButtonGroup,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  Label,
  FormGroup,
  Input,
  Table,
  Row,
  Col,
  UncontrolledTooltip,
} from "reactstrap";

function Dashboard() {
  const [active, setActive] = useState("ProductionOutputButton");
  const [MultiAxisLineChart_Example1, setMultiAxisLineChart] =
    React.useState("HourlyData");
  const set_Main_MultiAxisLineChart = (name) => {
    setMultiAxisLineChart(name);
  };

  const [LineChart_ProductionOutput_Example1, LineChart_ProductionOutput] =
    React.useState("HourlyData");
  const set_Main_LineChart_ProductionOutput = (name) => {
    LineChart_ProductionOutput(name);
  };

  const [LineChart_ProductionTime_Example1, LineChart_ProductionTime] =
    React.useState("HourlyData");
  const set_Main_LineChart_ProductionTime = (name) => {
    LineChart_ProductionTime(name);
  };

  const [LineChart_Defects_Example1, LineChart_Defects] =
    React.useState("HourlyData");
  const set_Main_LineChart_Defects = (name) => {
    LineChart_Defects(name);
  };

  const [LineChart_DefectRate_Example1, LineChart_DefectRate] =
    React.useState("HourlyData");
  const set_Main_LineChart_DefectRate = (name) => {
    LineChart_DefectRate(name);
  };





  return (
    <>
      <ButtonGroup
        className="btn-group-toggle float-right"
        data-toggle="buttons"
      >
        <Button
          tag="label"
          className={classNames("btn-simple", {
            active:
              MultiAxisLineChart_Example1 &&
              LineChart_ProductionOutput_Example1 &&
              LineChart_ProductionTime_Example1 &&
              LineChart_Defects_Example1 &&
              LineChart_DefectRate_Example1 === "HourlyData",
          })}
          color="info"
          id="0"
          size="sm"
          onClick={() => {
            set_Main_MultiAxisLineChart("HourlyData");
            set_Main_LineChart_ProductionOutput("HourlyData");
            set_Main_LineChart_ProductionTime("HourlyData");
            set_Main_LineChart_Defects("HourlyData");
            set_Main_LineChart_DefectRate("HourlyData");
          }}
        >
          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
            Hourly
          </span>
        </Button>
        <Button
          color="info"
          id="1"
          size="sm"
          tag="label"
          className={classNames("btn-simple", {
            active:
              MultiAxisLineChart_Example1 &&
              LineChart_ProductionOutput_Example1 &&
              LineChart_ProductionTime_Example1 &&
              LineChart_Defects_Example1 &&
              LineChart_DefectRate_Example1 === "DailyData",
          })}
          onClick={() => {
            set_Main_MultiAxisLineChart("DailyData");
            set_Main_LineChart_ProductionOutput("DailyData");
            set_Main_LineChart_ProductionTime("DailyData");
            set_Main_LineChart_Defects("DailyData");
            set_Main_LineChart_DefectRate("DailyData");
          }}
        >
          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
            Daily
          </span>
        </Button>
        <Button
          color="info"
          id="2"
          size="sm"
          tag="label"
          className={classNames("btn-simple", {
            active:
              MultiAxisLineChart_Example1 &&
              LineChart_ProductionOutput_Example1 &&
              LineChart_ProductionTime_Example1 &&
              LineChart_Defects_Example1 &&
              LineChart_DefectRate_Example1 === "MonthlyData",
          })}
          onClick={() => {
            set_Main_MultiAxisLineChart("MonthlyData");
            set_Main_LineChart_ProductionOutput("MonthlyData");
            set_Main_LineChart_ProductionTime("MonthlyData");
            set_Main_LineChart_Defects("MonthlyData");
            set_Main_LineChart_DefectRate("MonthlyData");
          }}
        >
          <span className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
            Monthly
          </span>
        </Button>
      </ButtonGroup>
      <div className="content">

      </div>


      <InfoCardRow infoCardsData={infoCardsData} />


    </>
  );
}

export default Dashboard;
