import React from 'react';
import * as FaIcons from 'react-icons/fa'
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import "./ProgressBar2.css";

const progressBarStyles = buildStyles({
  pathColor: `rgba(0, 0, 0, 1)`,
  textColor: 'black'
})

const ProgressBar2 = ({DefectState, ProductionLineState}) => {

if(ProductionLineState=="1"){

  return(

    <div className='PieChart'>
        {ProductionLineState}

    <div className='PieChart-header'>
        <FaIcons.FaPercentage className='PieChart-icon' size={20} color={'white'}/>
        <h3 className='PieChart-title'>Defect Rate</h3>

    </div>

    <div className='PieChart-body'>
        <div className='infocard'>

        <CircularProgressbar 
            className='progressbar' 
            value={"30"} 
            text={"30%"}
            strokeWidth={12}
            styles={progressBarStyles} />

            <h5 className='card-title1'>Defect Percentage Among Other Defects</h5>

         </div>



    </div>


    </div>











)
}
else {
    return(<> </>)
}
}

export default ProgressBar2;
