import React from "react";
import Chart from "react-apexcharts";
import "./HorizontalBarChart.css";
import * as FaIcons from 'react-icons/fa';
import {
  ProductionLineAll,
  ProductionLine1,
  ProductionLine2
} from "./MockData";

const HorizontalBarChart = ({ProductionLineState, DefectState}) => {
if(ProductionLineState== "All"){
  if(DefectState == "All"){
  var MockData =  ProductionLineAll.AllDefect;
  }
  if(DefectState == "SSCT"){
    var MockData =  ProductionLineAll.SSCT;
  }
  if(DefectState == "POCK"){
      var MockData =  ProductionLineAll.POCK;
  }
  if(DefectState == "POGR"){
    var MockData =  ProductionLineAll.POGR;
  }
  if(DefectState == "STANSTONE"){
    var MockData =  ProductionLineAll.STANSTONE;
  }
  if(DefectState == "STANWHITE"){
    var MockData =  ProductionLineAll.STANWHITE;
  }
}

if(ProductionLineState== "1"){
  if(DefectState == "All"){
  var MockData =  ProductionLine1.AllDefect;
  }
  if(DefectState == "SSCT"){
    var MockData =  ProductionLine1.SSCT;
  }
  if(DefectState == "POCK"){
      var MockData =  ProductionLine1.POCK;
  }
  if(DefectState == "POGR"){
    var MockData =  ProductionLine1.POGR;
  }
  if(DefectState == "STANSTONE"){
    var MockData =  ProductionLine1.STANSTONE;
  }
  if(DefectState == "STANWHITE"){
    var MockData =  ProductionLine1.STANWHITE;
  }
}

if(ProductionLineState== "2"){
  if(DefectState == "All"){
  var MockData =  ProductionLine2.AllDefect;
  }
  if(DefectState == "SSCT"){
    var MockData =  ProductionLine2.SSCT;
  }
  if(DefectState == "POCK"){
      var MockData =  ProductionLine2.POCK;
  }
  if(DefectState == "POGR"){
    var MockData =  ProductionLine2.POGR;
  }
  if(DefectState == "STANSTONE"){
    var MockData =  ProductionLine2.STANSTONE;
  }
  if(DefectState == "STANWHITE"){
    var MockData =  ProductionLine1.STANWHITE;
  }
}

if(ProductionLineState== "All" && DefectState == "SSCT"){
  var MockData =  SSCT_Defect;
}


      var state = {
        series: MockData.SeriesData,

        options: {
          chart: {
            type: 'bar',
            height: 350,
            stacked: true,
            stackType: '100%',
            toolbar: {show: false}},

          plotOptions: {
            bar: {
              horizontal: true,
              borderRadius : 0,}},

          stroke: {
            width: 1,
            colors: ['#fff']},

          xaxis: {           
            show: false,
            categories: [""],           
            labels: {show: false},
            axisBorder: {show: false}},

          grid:{
            show:false,
            borderColor: "#000000",
            strokeDashArray: 0,
            xaxis: {
                lines: {show: false}},
            yaxis: {
                lines: {show: true}},
            row: {
                colors: "#000000",
                opacity: 0.0}},

          legend: {
            position: 'top',
            horizontalAlign: 'center',
            offsetX: 40,
            offsetY: 20,
            fontSize: '15px',
            fontFamily: "Roboto",
            itemMargin: {horizontal: 10}},

          colors: [
            "#111111", "#333333", "#555555", "#777777", "#999999"]}};



      return (
            <div className="HorizontalBarChart" >
                <div className='HorizontalBarChart-header'>
                    <FaIcons.FaPercentage className='HorizontalBarChart-icon' size={20} color={'white'}/>
                    <h3 className='HorizontalBarChart-title'>Overall {MockData.title} Percentage</h3>
                </div>

                <div className='HorizontalBarChart-body'>
                        <Chart
                        options={state.options}
                        series={state.series}
                        type="bar"
                        height="240"
                        width= "1050"

                        />
                </div>
              </div>
      );
    }



  export default HorizontalBarChart;
