import React from 'react';

import ReactDOM from 'react-dom';
import {colors} from 'react-select';
import { VictoryPie, VictoryLabel } from 'victory';
import FlatList from 'flatlist-react';

const Home = () => {

const data = [
    {x: 1, y: 1},
    {x: 2, y: 2},
    {x: 3, y: 3},
    {x: 4, y: 4},
    {x: 5, y: 5},
    {x: 6, y: 6}
  ];
  let colorScales = data.map((item) => item.color);
  function pieLabelRadius(fraction) {
    if (fraction === 1) {
        return 0;
    } else if (fraction >= .5) {
        return 0.5;
    } else {
        const t = Math.tan(fraction * Math.PI / 2);
        return (2*t) / ((1+t)*(1+t));
    }
}

    // dummy data
    const confirmStatus = "C"
    const pendingStatus = "P"

    let categoriesData = [
        {
            id: 1,
            name: "Education",
            color: "#FFFF00",
            expenses: [
                {
                    id: 1,
                    title: "Tuition Fee",
                    description: "Tuition fee",
                    location: "ByProgrammers' tuition center",
                    total: 100.00,
                    status: pendingStatus
                },
                {
                    id: 2,
                    title: "Arduino",
                    description: "Hardward",
                    location: "ByProgrammers' tuition center",
                    total: 30.00,
                    status: pendingStatus
                },
                {
                    id: 3,
                    title: "Javascript Books",
                    description: "Javascript books",
                    location: "ByProgrammers' Book Store",
                    total: 20.00,
                    status: confirmStatus
                },
                {
                    id: 4,
                    title: "PHP Books",
                    description: "PHP books",
                    location: "ByProgrammers' Book Store",
                    total: 20.00,
                    status: confirmStatus
                }
            ],
        },
        {
            id: 2,
            name: "Nutrition",
            color: "#ADD8E6",
            expenses: [
                {
                    id: 5,
                    title: "Vitamins",
                    description: "Vitamin",
                    location: "ByProgrammers' Pharmacy",
                    total: 25.00,
                    status: pendingStatus,
                },

                {
                    id: 6,
                    title: "Protein powder",
                    description: "Protein",
                    location: "ByProgrammers' Pharmacy",
                    total: 50.00,
                    status: confirmStatus,
                },

            ],
        },
        {
            id: 3,
            name: "Child",
            color: "#008000",
            expenses: [
                {
                    id: 7,
                    title: "Toys",
                    description: "toys",
                    location: "ByProgrammers' Toy Store",
                    total: 25.00,
                    status: confirmStatus,
                },
                {
                    id: 8,
                    title: "Baby Car Seat",
                    description: "Baby Car Seat",
                    location: "ByProgrammers' Baby Care Store",
                    total: 100.00,
                    status: pendingStatus,
                },
                {
                    id: 9,
                    title: "Pampers",
                    description: "Pampers",
                    location: "ByProgrammers' Supermarket",
                    total: 100.00,
                    status: pendingStatus,
                },
                {
                    id: 10,
                    title: "Baby T-Shirt",
                    description: "T-Shirt",
                    location: "ByProgrammers' Fashion Store",
                    total: 20.00,
                    status: pendingStatus,
                },
            ],
        },
        {
            id: 4,
            name: "Beauty & Care",
            color: "#ffe5b4",
            expenses: [
                {
                    id: 11,
                    title: "Skin Care product",
                    description: "skin care",
                    location: "ByProgrammers' Pharmacy",
                    total: 10.00,
                    status: pendingStatus,
                },
                {
                    id: 12,
                    title: "Lotion",
                    description: "Lotion",
                    location: "ByProgrammers' Pharmacy",
                    total: 50.00,
                    status: confirmStatus,
                },
                {
                    id: 13,
                    title: "Face Mask",
                    description: "Face Mask",
                    location: "ByProgrammers' Pharmacy",
                    total: 50.00,
                    status: pendingStatus,
                },
                {
                    id: 14,
                    title: "Sunscreen cream",
                    description: "Sunscreen cream",
                    location: "ByProgrammers' Pharmacy",
                    total: 50.00,
                    status: pendingStatus,
                },
            ],
        },
        {
            id: 5,
            name: "Sports",
            color: "#800080", 
            expenses: [
                {
                    id: 15,
                    title: "Gym Membership",
                    description: "Monthly Fee",
                    location: "ByProgrammers' Gym",
                    total: 45.00,
                    status: pendingStatus,
                },
                {
                    id: 16,
                    title: "Gloves",
                    description: "Gym Equipment",
                    location: "ByProgrammers' Gym",
                    total: 15.00,
                    status: confirmStatus,
                },
            ],
        },
        {
            id: 6,
            name: "Clothing",
            color: "ff0000",
            expenses: [
                {
                    id: 17,
                    title: "T-Shirt",
                    description: "Plain Color T-Shirt",
                    location: "ByProgrammers' Mall",
                    total: 20.00,
                    status: pendingStatus,
                },
                {
                    id: 18,
                    title: "Jeans",
                    description: "Blue Jeans",
                    location: "ByProgrammers' Mall",
                    total: 50.00,
                    status: confirmStatus,
                },
            ],
        }
    ]

const [categories, setCategories] = React.useState(categoriesData)
const [selectedCategory, setSelectedCategory] = React.useState(null)


function processCategoryDataToDisplay() {
    // Filter expenses with "Confirmed" status

    let chartData = categories.map((item) => {
        let confirmExpenses = item.expenses.filter(a => a.status == "C")
        var total = confirmExpenses.reduce((a, b) => a + (b.total || 0), 0)

        return {
            name: item.name,
            y: total,
            expenseCount: confirmExpenses.length,
            color: item.color,
            id: item.id
        }
    })

    // filter out categories with no data/expenses
    let filterChartData = chartData.filter(a => a.y > 0)

    // Calculate the total expenses
    let totalExpense = filterChartData.reduce((a, b) => a + (b.y || 0), 0)

    // Calculate percentage and repopulate chart data
    let finalChartData = filterChartData.map((item) => {
        let percentage = (item.y / totalExpense * 100).toFixed(0)
        return {
            label: `${percentage}%`,
            y: Number(item.y),
            expenseCount: item.expenseCount,
            color: item.color,
            name: item.name,
            id: item.id
        }
    })

    return finalChartData
}

function setSelectCategoryByName(name) {
    let category = categories.filter(a => a.name == name)
    setSelectedCategory(category[0])
}

function renderChart() {
    let chartData = processCategoryDataToDisplay()
    let colorScales = chartData.map((item) => item.color)
    let totalExpenseCount = chartData.reduce((a, b) => a + (b.expenseCount || 0), 0)



    return (
        <div  style={{ alignItems: 'center', justifyContent: 'center' }}>
                               <VictoryPie

                        data={chartData}
                        labels={(datum) => `${datum.y}`}
                        radius={({ datum }) => (selectedCategory && selectedCategory.name == datum.name) ? 400 * 0.4 : 400* 0.4 - 10}
                        innerRadius={70}
                        labelRadius={({ innerRadius }) => (400 * 0.4 + innerRadius) / 2.5}
                        style={{
                            labels: { fill: "white" },
                        }}
                        width={400 * 0.8}
                        height={400 * 0.8}
                        colorScale={colorScales}
                        events={[{
                            target: "data",
                            eventHandlers: {
                                onClick: () => {
                                    return [{
                                        target: "labels",
                                        mutation: (props) => {
                                            let categoryName = chartData[props.index].name
                                            setSelectCategoryByName(categoryName)
                                        }
                                    }]
                                }
                            }
                        }]}

                    /> 
                    <div style={{ position: 'absolute', top: '62%', left: "42%" }}>
                        <div style={{ textAlign: 'center' }}>{totalExpenseCount}</div>
                        <div style={{ textAlign: 'center' }}>Expenses</div>
                    </div>
        </div>
    )

};

function renderExpenseSummary() {
    let data = processCategoryDataToDisplay()

    const renderItem = ({ item }) => (
        <div
            style={{
                flexDirection: 'row',
                height: 40,
                // paddingHorizontal: SIZES.radius,
                borderRadius: 10,
                backgroundColor: (selectedCategory && selectedCategory.name == item.name) ? item.color : "#ffffff"
            }}
            onPress={() => {
                let categoryName = item.name
                setSelectCategoryByName(categoryName)
            }}
        >
            {/* Name/Category */}
            <div style={{ flex: 1, flexDirection: 'row', alignItems: 'center' }}>
                <div
                    style={{
                        width: 20,
                        height: 20,
                        backgroundColor: (selectedCategory && selectedCategory.name == item.name) ? "#ffffff" : item.color,
                        borderRadius: 5
                    }}
                />

                <div style={{ color: (selectedCategory && selectedCategory.name == item.name) ? "#ffffff" : "#000000"}}>{item.name}</div>
            </div>

            {/* Expenses */}
            <div style={{ justifyContent: 'center' }}>
                <div style={{ color: (selectedCategory && selectedCategory.name == item.name) ? "#ffffff" : "#000000"}}>{item.y} USD - {item.label}</div>
            </div>
        </div>
    )

    return (
        <div style={{ padding: 0}}>
            <FlatList
                data={data}
                renderItem={renderItem}
                keyExtractor={item => `${item.id}`}
            />
        </div>

    )
};





return(
    <div>
       {renderChart()} 
       {renderExpenseSummary()}
    </div>
)
}

export default Home;
