import React from 'react';
import ReactDOM from 'react-dom';
import {colors} from 'react-select';
import { VictoryPie } from 'victory';


const data = [
    {quarter: 1, earnings: 13000},
    {quarter: 2, earnings: 16500},
    {quarter: 3, earnings: 14250},
    {quarter: 4, earnings: 19000}
  ];
  let colorScales = data.map((item) => item.color);
  function pieLabelRadius(fraction) {
    if (fraction === 1) {
        return 0;
    } else if (fraction >= .5) {
        return 0.5;
    } else {
        const t = Math.tan(fraction * Math.PI / 2);
        return (2*t) / ((1+t)*(1+t));
    }
}
class PieChart extends React.Component {


    render() {
      return (

        <VictoryPie
          data={data}
          colorScales={colorScales}
          x="quarter"
          y="earnings"
          labels={ datum => '$'+datum.value.toFixed(2) }
          radius={100}
          innerRadius={45}
          labelRadius={ (args) => 400*(1-pieLabelRadius(args.value/400)) }
          style={{
            labels: {
                textAnchor: "middle",
                verticalAnchor: "middle",
            }
        }}


        //   height={SIZES.width * 0.8}

        />
      )
    }
}

export default PieChart;
