import React from "react";
import Chart from "react-apexcharts";
import "./HorizontalBarChart.css";
import * as FaIcons from 'react-icons/fa';



const HorizontalBarChart = ({ProductionLineState, DefectState}) => {


      var state = {
        series: [
          {name: 'POCK', data: [44]},
          {name: 'SSCT', data: [53]},
          {name: 'POGR', data: [12]},
          {name: 'STAN', data: [9]},
          {name: 'OTHERS', data: [27]}],

        options: {
          chart: {
            type: 'bar',
            height: 350,
            stacked: true,
            stackType: '100%',
            toolbar: {show: false}},

          plotOptions: {
            bar: {
              horizontal: true,
              borderRadius : 0,}},

          stroke: {
            width: 1,
            colors: ['#fff']},

          xaxis: {           
            show: false,           
            labels: {show: false},
            axisBorder: {show: false}},

          grid:{
            show:false,
            borderColor: "#000000",
            strokeDashArray: 0,
            xaxis: {
                lines: {show: false}},
            yaxis: {
                lines: {show: true}},
            row: {
                colors: "#000000",
                opacity: 0.0}},

          legend: {
            position: 'top',
            horizontalAlign: 'center',
            offsetX: 40,
            offsetY: 20,
            fontSize: '15px',
            fontFamily: "Roboto",
            itemMargin: {horizontal: 10}},

          colors: [
            "#111111", "#333333", "#555555", "#777777", "#999999"
          ],
        },


      };



      return (

            <div className="HorizontalBarChart" >
                <div className='HorizontalBarChart-header'>
                    <FaIcons.FaPercentage className='HorizontalBarChart-icon' size={20} color={'white'}/>
                    <h3 className='HorizontalBarChart-title'>Overall Defect Percentage</h3>
                </div>

                <div className='HorizontalBarChart-body'>

                        <Chart
                        options={state.options}
                        series={state.series}
                        type="bar"
                        height="240"
                        width= "1050"

                    />

              </div>
              </div>

      );
    }



  export default HorizontalBarChart;
