import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import {Pie} from 'react-chartjs-2';
import 'chart.piecelabel.js';







class PieChart extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            labels: ['DEFECT1', 'DEFECT2', 'DEFECT3', 'DEFECT4','DEFECT4', 'DEFECT5'],
            backgroundColor: '#000000',
            datasets: [{
                data: [45, 80, 13, 23, 48, 32],
                backgroundColor: ['#4a4a4a', '#545454', '#696969', '#8f8f8f', '#adadad', '#bababa']
            }],



        }
    }

    render() {
      return (

        <>

        <div style={{position: "relative", right:'40px',top:'30px', paddingBottom:'100px'}}>
        <Pie
        data={{
            labels: this.state.labels,
            datasets: this.state.datasets,

        }}
        options={{
            legend: {display: false, postion: 'left'},
            responsive: true,
            datalabels: {
                display: true,
                color: "white",
              },
              tooltips: {
                backgroundColor: "#5a6e7f",
              },
              pieceLabel: {
                render: 'value',
                fontColor: 'white',
                fontSize: 25,
                arc: true

             }
        }}
        height='100%'
         />
         </div>



        </>
      )
    }
}

export default PieChart;
